import React, { useState } from "react";
import BookCard from "./cards/BookCard";
import "../css/Main.css";
import { Link } from "react-router-dom";
import "../css/BookList.css";
import { Button } from "@material-ui/core";

function BookList(props) {
  let [data, setData] = useState[props.data];
  let data1 = props.data;
  let booklist = props.booklist;
  console.log("data");
  console.log(data1);
  let IT = "IT융합자율학부";
  let humanities = "인문융합자율학부";
  let society = "사회융합자율학부";
  let mediacontents = "미디어콘텐츠융합자율학부";
  let culture = "교양";
  let list = data ? data : booklist;
  console.log(list);
  const handleAdd = (e) => {
    props.handleBookDetail(e.id);
  };
  const handleLike = (e) => {
    props.handleLike(e.id);
  };
  const handleData = () => {
    setData(booklist[0], booklist[1], booklist[2]);
  };
  return (
    <>
      <div className="subjectList">
        <Button
          variant="contained"
          value="subject"
          onClick={() => {
            props.handleAll();
            handleData();
          }}
        >
          ALL
        </Button>
        <Button
          variant="contained"
          value="subject"
          onClick={() => {
            props.handleSubject(humanities);
            handleData();
          }}
        >
          인문
        </Button>
        <Button
          variant="contained"
          value="subject"
          onClick={() => {
            props.handleSubject(society);
            handleData();
          }}
        >
          사회
        </Button>
        <Button
          variant="contained"
          value="subject"
          onClick={() => {
            props.handleSubject(mediacontents);
            handleData();
          }}
        >
          미콘
        </Button>
        <Button
          variant="contained"
          value="subject"
          onClick={() => {
            props.handleSubject(IT);
            handleData();
          }}
        >
          IT
        </Button>
        <Button
          variant="contained"
          value="subject"
          onClick={() => {
            props.handleSubject(culture);
            handleData();
          }}
        >
          교양
        </Button>
      </div>
      {list ? (
        <div className="bookWrap">
          {list.map((d) => (
            <div className="bookList">
              <div className="bookCard">
                <BookCard
                  image={d.image}
                  title={d.title}
                  author={d.author}
                  publisher={d.publisher}
                  pubDate={d.pubdate}
                  isbn={d.isbn}
                  subject={d.subject}
                  className={d.className}
                  professor={d.professor}
                  stock={d.stock}
                />
                <button className="buttonHide" onClick={() => handleLike(d)}>
                  {d.like === 0 ? (
                    <i class="far fa-thumbs-up"></i>
                  ) : (
                    <i class="fas fa-thumbs-up"></i>
                  )}
                </button>

                <Link to={`/bookdetail/${d.id}`}>
                  <button value="add" onClick={() => handleAdd(d)}>
                    자세히
                  </button>
                </Link>
              </div>
            </div>
          ))}
        </div>
      ) : (
        "버튼을 클릭하세요"
      )}
    </>
  );
}

export default BookList;
